if (data.attachment.raw) {
    for (var key in data.attachment)
        data[key] = data.attachment[key];
    if (!data.reply_markup) data.reply_markup = {};
} else GetTelegramAttachment(data.attachment);

function GetTelegramAttachment(obj) {
    switch (obj.type) {
        case "buttons":
            data.message = {
                "attachment": {
                    "payload": {
                        "buttons": data.attachment.buttons,
                        "template_type": "button",
                        "text": data.text
                    },
                    "type": "template"
                }
            };
            break;
        case "quick_replies":
            data.message = {
                "text": data.text,
                "quick_replies": data.attachment.buttons
            };
            break;
        case "carousel":
            data.message = {
                "attachment": {
                    "type": "template",
                    "payload": {
                        "template_type": "generic",
                        "elements": data.attachment.items
                    }
                }
            };
            break;
        case "carousel_with_keyboard":
            data.message = {
                "quick_replies": data.attachment.buttons,
                "attachment": {
                    "type": "template",
                    "payload": {
                        "template_type": "generic",
                        "elements": data.attachment.items
                    }
                }
            };
            break;
        case "list":
            data.message = {
                "attachment": data.attachment.items
            };
            break;
        case "invoice":
            data.message = {
                "attachment": {
                    "type": "template",
                    "payload": data.attachment
                }
            };
            break;
        case "image":
        case "video":
        case "audio":
        case "file":    
            data.message = {
                "attachment": {
                    "type": data.attachment.type,
                    "payload": {
                        "url": data.attachment.url,
                        "is_reusable": true
                    }
                }
            };
            break;
        default:
            data.message = {
                "text": data.text
            };
            break;
    }

}

if (data.attachment.raw) {
    for (var key in data.attachment)
        data[key] = data.attachment[key];
    if (!data.reply_markup) data.reply_markup = {};
} else GetTelegramAttachment(data.attachment);

function GetTelegramAttachment(obj) {
    switch (obj.type) {
        case "inline_keyboard":
            data.reply_markup = {
                "inline_keyboard": data.attachment.buttons
            };
            if (data.attachment.parse_mode) data.parse_mode = data.attachment.parse_mode;
            else data.parse_mode = "HTML";
            break;
        case "keyboard":
            data.reply_markup = {
                "keyboard": data.attachment.buttons,
                "one_time_keyboard": true,
                "resize_keyboard": true
            };
            if (data.attachment.parse_mode) data.parse_mode = data.attachment.parse_mode;
            else data.parse_mode = "HTML";
            break;
        case "image":
        case "video":
        case "audio":
            data.url = data.attachment.url;
            if (data.attachment.caption) data.text = data.attachment.caption;
            data.reply_markup = {};
            break;
        case "location":
            data.lat = data.attachment.lat;
            data.lon = data.attachment.lon;
            data.reply_markup = {};
            break;
        default:
            data.reply_markup = {};
            break;
    }

}

