function GetViberAttachment(obj) {
    switch (obj.type) {
        case "keyboard":
            data.keyboard = {
                "Type": "keyboard",
                "Buttons": data.attachment.buttons
            };
            if (data.attachment.inputFieldState) data.keyboard.InputFieldState = data.attachment.inputFieldState;
            data.rich_media = {};
            break;
        case "carousel":
            data.rich_media = {
                "Type": "rich_media",
                "ButtonsGroupColumns": data.attachment.carouselColumns,
                "ButtonsGroupRows": data.attachment.carouselRows,
                "BgColor": "#FFFFFF",
                "Buttons": data.attachment.items
            };
            if (data.attachment.buttons && data.attachment.buttons.length > 0) {
                data.keyboard = {
                    "Type": "keyboard",
                    "Buttons": data.attachment.buttons
                };
                data.attachment.type = "carousel_with_keyboard";
            } else data.keyboard = {};
            break;
        case "carousel_with_keyboard":
            data.rich_media = {
                "Type": "rich_media",
                "ButtonsGroupColumns": data.attachment.carouselColumns,
                "ButtonsGroupRows": data.attachment.carouselRows,
                "BgColor": "#FFFFFF",
                "Buttons": data.attachment.items
            };
            data.keyboard = {
                "Type": "keyboard",
                "Buttons": data.attachment.buttons
            };
            if (data.attachment.inputFieldState) data.keyboard.InputFieldState = data.attachment.inputFieldState;
            break;
        case "image":
            data.media = data.attachment.url;
            data.rich_media = {};
            data.keyboard = {};
            break;
        case "video":
            data.media = data.attachment.url;
            data.size = data.attachment.size;
            data.rich_media = {};
            data.keyboard = {};
            break;
        case "audio":
        case "file":
            data.media = data.attachment.url;
            data.size = data.attachment.size;
            data.file_name = data.attachment.name;
            data.rich_media = {};
            data.keyboard = {};
            break;
        case "location":
            data.lat = attachment.lat;
            data.lon = attachment.lon;
            data.rich_media = {};
            data.keyboard = {};
            break;
        default:
            data.rich_media = {};
            data.keyboard = {};
            break;
    }
}

var re = /{{(\w+\.?)+}}/ig;
var channel = data.channel;
var attachment = data.attachment;
var items = [];
var itemOptions = [];
var buttons = [];
var itemsPattern = attachment.items;
var buttonsPattern = attachment.buttons;
if (attachment.apple_pay_request) {
    var paymentItemsPattern = attachment.apple_pay_request.payment.paymentRequest.lineItems;
}


// PAGINATION
var totalPages = 0;
var maxItemsCountToShow = 0;
var currentPage = parseInt(data.currentPage) || 0;
var currentOffset = 0;

if (!data.attachment.buttons) {
    data.attachment.buttons = [];
}


// Get max. items count to render
if (!data.attachment.maxItemsCountToShow && !data.maxItemsCountToShow) {
    switch (channel) {
        case "facebook":
            if (attachment.type === "carousel" || attachment.type === "carousel_with_keyboard") {
                maxItemsCountToShow = 10;
            }
            if (attachment.type === "quick_replies") {
                maxItemsCountToShow = 6;
            }
            if (attachment.type === "list") {
                maxItemsCountToShow = 4;
            }
            break;
        case "viber":
            if (attachment.type === "carousel" || attachment.type === "carousel_with_keyboard") {
                maxItemsCountToShow = 6;
            }
            if (attachment.type === "keyboard") {
                maxItemsCountToShow = 8;
            }
            break;
        case "telegram":
            if (attachment.type === "inline_keyboard") {
                maxItemsCountToShow = 5;
            } else {
                maxItemsCountToShow = 10;
            }
            break;
        case "abc":
            if (attachment.type === "apple_pay_request") {
                maxItemsCountToShow = 10;
            }
    }
} else {
    maxItemsCountToShow = parseInt(data.attachment.maxItemsCountToShow) || parseInt(data.maxItemsCountToShow);
}

// Increment page controller
if (currentPage !== 0) {
    currentOffset = maxItemsCountToShow * currentPage;
} else {
    currentOffset = maxItemsCountToShow;
}


// Get total pages
totalPages = Math.ceil(data.items.length / maxItemsCountToShow);

// Slice array by pagination offset
itemOptions = data.items.slice(currentOffset - maxItemsCountToShow, currentOffset);

// Create Apple Pay dynamic items
if (attachment.type === "apple_pay_request") {
    createDynamicAttachment(items, data.items, paymentItemsPattern, re);
    data.attachment.apple_pay_request.payment.paymentRequest.lineItems = items;
}

// Create dynamic carousel
if (attachment.items && data.items) {
    if (attachment.type == "image_list_picker") {
        var images = [];
        var imagesPattern = attachment.images;
	    createDynamicAttachment(images, itemOptions, imagesPattern, re);
        data.attachment.images = images;
    }
    createDynamicAttachment(items, itemOptions, itemsPattern, re);
    data.attachment.items = items;
}

if (attachment.type === "keyboard" || attachment.type === "quick_replies" || attachment.type === "inline_keyboard") {
    data.buttons = itemOptions;
}
// Create dynamic keyboard
if (attachment.buttons && data.buttons) {
    createDynamicAttachment(buttons, data.buttons, buttonsPattern, re);
    data.attachment.buttons = buttons;
}

data.totalPages = totalPages;

// Create pagination buttons
if (totalPages > 1) {
    var paginationButtons = createPaginationButtons(currentPage, totalPages, channel);
    paginationButtons.forEach(function(button) {
        if (channel === 'abc') {
            //             data.attachment.items.push(button);
            data.attachment.buttons.push(button);
        } else {
            if (attachment.type === "keyboard" || attachment.type === "quick_replies" || attachment.type === "inline_keyboard") {
                data.attachment.buttons.push(button);
            } else {
                data.attachment.buttons.unshift(button);
            }
        }
    });
}

function createPaginationButtons(currentPage, totalPages, channel) {
    var r = [];

    var emptyViberButton = {
        "Columns": "2",
        "Rows": 1,
        "BgColor": "#F3F3F3",
        "Text": "",
        "ActionType": "reply",
        "ActionBody": "none",
        "TextVAlign": "middle",
        "TextHAlign": "center",
        "TextSize": "regular",
        "Silent": true
    };

    var paginationPatterns = {
        "viber": [{
                "Columns": "2",
                "Rows": 1,
                "BgColor": "#F3F3F3",
                "Text": "{{t'prev}}",
                "ActionType": "reply",
                "ActionBody": "prev",
                "TextVAlign": "middle",
                "TextHAlign": "center",
                "TextSize": "regular",
                "Silent": true
            },
            {
                "Columns": "2",
                "Rows": 1,
                "BgColor": "#F3F3F3",
                "Text": currentPage + " {{t'of}} " + totalPages,
                "ActionType": "reply",
                "ActionBody": "none",
                "TextVAlign": "middle",
                "TextHAlign": "center",
                "TextSize": "regular",
                "Silent": true
            },
            {
                "Columns": "2",
                "Rows": 1,
                "BgColor": "#F3F3F3",
                "Text": "{{t'next}}",
                "ActionType": "reply",
                "ActionBody": "next",
                "TextVAlign": "middle",
                "TextHAlign": "center",
                "TextSize": "regular",
                "Silent": true
            }
        ],
        "facebook": [{
                "content_type": "text",
                "title": "{{t'prev}}",
                "payload": "prev"
            },
            {
                "content_type": "text",
                "title": currentPage + " {{t'of}} " + totalPages,
                "payload": "none"
            },
            {
                "content_type": "text",
                "title": "{{t'next}}",
                "payload": "next"
            }
        ],
        "telegram": [
            [{
                    "text": "{{t'prev}}",
                    "callback_data": "prev"
                },
                {
                    "text": currentPage + " {{t'of}} " + totalPages,
                    "callback_data": "none"
                },
                {
                    "text": "{{t'next}}",
                    "callback_data": "next"
                }
            ]
        ],
        "abc": [{
                "identifier": "prev",
                "order": currentOffset + 1,
                "style": "small",
                "subtitle": "",
                "title": "{{t'prevPage}}"
            },
            //             {
            //                 "identifier": "pagination",
            //                 "order": currentOffset+2,
            //                 "style": "small",
            //                 "subtitle": "",
            //                 "title": "{{t'page}}: " + currentPage + " {{t'of}} " + totalPages
            //             },
            {
                "identifier": "next",
                "order": currentOffset + 3,
                "style": "small",
                "subtitle": "",
                "title": "{{t'nextPage}}"
            }
        ]
    };

    // Iterate through the buttons
    paginationPatterns[channel].forEach(function(item, i) {
        if (channel === "viber") {
            // Hide "Prev" button at the first page
            if (i === 0 && currentPage === 1) {
                item = emptyViberButton;
            }
            // Hide "Next" button at the last page
            if (i === 2 && currentPage === totalPages) {
                item = emptyViberButton;
            }
        }
        if (channel === "facebook") {
            // Hide "Prev" button at the first page 
            if (i === 0 && currentPage === 1) {
                item = "";
            }
            // Hide "Next" button at the last page
            if (i === 2 && currentPage === totalPages) {
                item = "";
            }
        }
        if (channel === "abc") {
            // Hide "Prev" button at the first page 
            if (i === 0 && currentPage === 1) {
                item = "";
            }
            // Hide "Next" button at the last page
            if (i === 1 && currentPage === totalPages) {
                item = "";
            }
        }
        if (channel === "telegram") {
            // Hide "Prev" button at the first page 
            if (i === 0 && currentPage === 1) {
                item[0].text = "-";
                item[0].callback_data = "-";
            }
            // Hide "Next" button at the last page
            if (currentPage === totalPages) {
                item[2].text = "-";
                item[2].callback_data = "-";
            }
        }

        if (item) {
            if (attachment.type === "quick_replies" || attachment.type === "keyboard" || attachment.type === "inline_keyboard") {
                r.push(item);
            } else {
                r.unshift(item);
            }
        }
    });

    return r;
}

function createDynamicAttachment(target, source, patterns, re) {
    source.forEach(function(item, i, l) {

        // Viber Keyboard
        item.buttonColumns = 3;
        // Create rows with 2 buttons
        if (l.length <= 5 || l.length % 2 === 0 && l.length < 8) {
            item.buttonColumns = 3;
        }
        // Create rows with 3 buttons
        if (l.length % 3 === 0) {
            item.buttonColumns = 2;
        }
        // Set last button width to 100%
        // if there are 5 or 7 buttons
        if (l.length > 4 && l.length < 8) {
            if (l.length % 3 === 1 || l.length % 2 === 1) {
                if (i === l.length - 1) {
                    item.buttonColumns = 6;
                }
            }
        }
        // Set last buttons width to 50%
        // if there are 8 buttons
        if (l.length == 8) {
            if (i >= 6) {
                item.buttonColumns = 3;
            }
        }

        if (l.length === 1) {
            item.buttonColumns = 6;
        }

        if (i < maxItemsCountToShow) {
            patterns.forEach(function(pattern) {
                var p = {};
                if (Array.isArray(pattern)) {
                    p = deepCopy(pattern);
                } else {
                    p = deepCopy(pattern);
                }
                replaceVariables(p, item, re);
                target.push(p);
            });
        }
    });
}

function deepCopy(o) {
    return JSON.parse(JSON.stringify(o));
}

function replaceVariables(objectToReplace, dataSource, regExp) {
    if (typeof objectToReplace === "string") {
        return matchToReplace(objectToReplace, dataSource, regExp);
    } else {
        for (var k in objectToReplace) {
            var s = getValueByKey(k, objectToReplace);
            if (typeof s === 'object') {
                s = replaceVariables(s, dataSource, regExp);
            }
            if (typeof s === "string") {
                objectToReplace[k] = matchToReplace(s, dataSource, regExp);
            }
        }
    }
}

function getValueByKey(key, object) {
    if (object.hasOwnProperty(key)) {
        return object[key];
    }
}

function matchToReplace(stringToReplace, dataSource, regExp) {
    if (typeof stringToReplace === 'string') {
        var d = stringToReplace.match(regExp);
        if (d !== null && d !== undefined && d[0] !== "") {
            for (var i = 0; i < d.length; i++) {

                v = d[i].replace(/{{/, "").replace(/}}/, "");
                if (v !== undefined) {
                    var arrayOfKeys = v.split(".");
                    if (arrayOfKeys.length > 1) {
                        var o = dataSource;
                        for (var k in arrayOfKeys) {
                            var s = arrayOfKeys[k];
                            o = o[s];
                            if (typeof o === "string" || typeof o === "number") {
                                stringToReplace = stringToReplace.replace(d[i], o);
                            }

                        }
                    } else {
                        if (dataSource[v] !== undefined) {
                            stringToReplace = stringToReplace.replace(d[i], dataSource[v]);
                        }
                    }
                }
            }

        }
    }
    return stringToReplace;
}

